import React, { useState, useContext } from 'react';
import AuthContext from '../../../context/authContext/AuthContext'
import { View, Text, Button, Image, TouchableOpacity, StyleSheet, TextInput } from 'react-native';
import Accounts from '../../../models/auth/data.json'
import logo from '../../../assets/logo.jpg'


const Login = () => {

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const { onAuthentication } = useContext(AuthContext)

  const onUserAuthentication = () => {
    const authInfo = Accounts.find((element) => {
      return element.email === email && element.password === password
    })
    if (
      authInfo  == null
    ) {
      alert('The email or password is incorrect')
      return

    }
    onAuthentication()
    console.log(authInfo)
    console.log(Accounts)
  }

  const onInputChange = (value, setState) => {
    setState(value);
  }

  return (
    <View style={styles.container}>
      <View style={styles.imageArea}>
        <Image source={logo} style={{width: 190, height: 73, marginTop: 51}}/> 
      </View>

      <View style={styles.inputArea}>
        <Text>Email</Text>
        <TextInput
          style={styles.inputs}
          placeholder="Enter your email here.."
          value={email}
          onChangeText={(value) => onInputChange(value, setEmail)}
        />
        <Text>Password</Text>
        <TextInput
          style={styles.inputs}
          secureTextEntry={true}
          placeholder="Enter your password here.."
          value={password}
          onChangeText={(value) => onInputChange(value, setPassword)}
        />
        <Button style={styles.buttonLogin}  title="AUTHENTICATE" onPress={onUserAuthentication} />
      </View>

      <View style={styles.footArea}>

      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 20, 
  },

  inputs: {
    width: 320,
    height: 49,
    marginBottom: 10,
    borderWidth: 1,
    borderRadius: 8,
    borderColor: '#C0C0C0'
  },
  buttonLogin:{
    borderRadius: 15
  },
  imageArea:{
    flex: 1
  },
  inputArea:{
    flex: 2
  },
  footArea:{
    flex: 1
  },

})

export default Login
